(function() {
  var Success, assert;

  assert = require('../assert');

  Success = require('data.validation').Success;

  module.exports = function(type) {
    assert.isFunction(type);
    return function(input) {
      if (input != null) {
        return type(input);
      } else {
        return Success(null);
      }
    };
  };

}).call(this);
